mapper.update:

	UPDATE	COMM_TRAVELREVIEW
	SET		CONTENT	= #{CONTENT}
	WHERE	IDX		= #{IDX}

mapper.select:

	SELECT	IDX
	FROM	COMM_TAGS
	WHERE	TAGNAME = #{0}

mapper.update:

	DELETE FROM	COMM_TAGMAP
	WHERE		ARTICLEID = #{IDX} 
			AND TAG_ID = #{TAGID}
			AND COMMTYPE = 1


	UPDATE	COMM_TRAVELREVIEW
	SET		TAG = #{TAG}
	WHERE	IDX = #{IDX}


	UPDATE COMM_TRAVELREVIEW 
	SET
		SUBJECT = #{SUBJECT},
		CONTENT = #{CONTENT}
	WHERE
		IDX = #{IDX}
